//Fetchonecard Returns the card with an ID or slug that matches the one you specify. For more information, see the <a href='/documentation/hearthstone/guides/card-search'>Card Search Guide</a>.
//
//@see: /hearthstone/cards/:idorslug
func StringFetchonecard(ctx context.Context, fields *FetchonecardFields)(string,error){
	if fields == nil {
		fields = &FetchonecardFields{}
	}

	func(input *FetchonecardFields) {
	
if input.Idorslug==""{
input.Idorslug = "52119-arch-villain-rafaam"
}

	
if input.Locale==""{
input.Locale = "en_US"
}

	
if input.GameMode==""{
input.GameMode = "<no value>"
}

	
		input.RequestMethod = utils.RequestMethod{
			Methods: "GET",
			Path: "/hearthstone/cards/:idorslug",
			CnRegion:false ,
		}
	}(fields)
	cli := http.Client{}
	req, err := http.NewRequestWithContext(
		ctx,
		fields.Methods,
		"https://us.api.blizzard.com"+fields.Path,
		nil)
	
	if err != nil {
		return "", err
	}
	req.Header.Add("Authorization", "Bearer "+Authentication.GetToken())
	q := req.URL.Query()
	q.Add("idorslug", fields.Idorslug)
	q.Add("locale", fields.Locale)
	q.Add("gameMode", fields.GameMode)
	
	req.URL.RawQuery = q.Encode()

	resp, err := cli.Do(req)
	if err != nil {
		return "", err
	}
	defer resp.Body.Close()

	responseBytes, err := io.ReadAll(resp.Body)
	if err != nil {
		return "", err
	}

	return string(responseBytes), nil
}

func CNFetchonecard(ctx context.Context, fields *FetchonecardFields)(any,error){

obj, err := StringFetchonecard(ctx,fields)
if err != nil {
		return nil,err
	}
resp :=&FetchonecardResponse{}
err = json.Unmarshal([]byte(obj), resp )
	if err != nil {
		return nil,err
	}

return resp,nil 
}

